package ex;
/*
 * Topic: 輸入一正整數，判斷其奇偶數。
 * Date: 2016/10/03
 * Author: 105021031 孟京澤
 */
import java.util.*;
public class ex05_105021031 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner src=new Scanner(System.in);
		int a=src.nextInt();
		if(a%2==0)
		{
			System.out.print("偶數");
		}
		else
		{
			System.out.print("奇數");
		}
		
	}

}
